import React from "react";
// import "./Header.css";
import {
  Stack,
  Button,
  AppBar,
  Toolbar,
  IconButton,
  Typography,
  Card,
  CardActions,
  Box,
  CardContent,
  CardHeader,
  Grid,
  TextField,
  InputAdornment,
  Fab,
  Checkbox,
} from "@mui/material";
import TaskAltIcon from "@mui/icons-material/TaskAlt";
import TaskIcon from "@mui/icons-material/Task";
import AddTaskIcon from "@mui/icons-material/AddTask";
import CheckCircleOutlineIcon from "@mui/icons-material/CheckCircleOutline";
import CheckCircleIcon from "@mui/icons-material/CheckCircle";

class Header extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      numberOfTasks: 0,
    };
  }
  handleClick = () => {
    this.setState((previousState) => ({
      numberOfTasks: previousState.numberOfTasks + 1,
    }));
  };

  render() {
    const navBarComponent = (
      <AppBar position="static">
        <Toolbar>
          <IconButton size="large" edge="start" color="inherit">
            <TaskAltIcon></TaskAltIcon>
          </IconButton>
          <Typography
            variant="h6"
            component="div"
            sx={{ flexGrow: 1, textAlign: "left" }}
          >
            Task Manager
          </Typography>
        </Toolbar>
      </AppBar>
    );

    const taskComponent = () => {
      return (
        <Stack direction="row">
          <Checkbox
            icon={<CheckCircleOutlineIcon />}
            checkedIcon={<CheckCircleIcon />}
          ></Checkbox>
          <Typography></Typography>
        </Stack>
      );
    };

    return (
      <>
        {navBarComponent}

        <Grid container sx={{ justifyContent: "center", my: 10 }}>
          <Grid item sx={{ display: "flex", flexDirection: "column" }}>
            <Card
              raised={true}
              sx={{
                width: 500,
                display: "flex",
                flexDirection: "column",
                // height: 250,
              }}
            >
              <CardHeader
                avatar={<TaskAltIcon></TaskAltIcon>}
                title="Create New Task"
                disableTypography={false}
                sx={{
                  textAlign: "left",
                  backgroundColor: "grey",
                }}
              ></CardHeader>
              <CardContent>
                <Stack direction="row">
                  <TextField
                    variant="outlined"
                    InputProps={{
                      startAdornment: (
                        <InputAdornment position="start">
                          <TaskIcon />
                        </InputAdornment>
                      ),
                    }}
                    sx={{ flexGrow: 1 }}
                  ></TextField>
                  <Fab
                    color="primary"
                    aria-label="add"
                    sx={{ ml: 2 }}
                    onClick={this.handleClick}
                  >
                    <AddTaskIcon></AddTaskIcon>
                  </Fab>
                </Stack>
              </CardContent>

              <CardContent>
                <Stack direction="column">
                  {[...Array(this.state.numberOfTasks)].map((item) => {
                    return taskComponent();
                  })}
                </Stack>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </>
    );
  }
}

export default Header;
