import { useState, useEffect } from "react";
import axios from "axios";
// import "./Header.css";
import {
  Stack,
  Button,
  AppBar,
  Toolbar,
  IconButton,
  Typography,
  Card,
  CardActions,
  Box,
  CardContent,
  CardHeader,
  Grid,
  TextField,
  InputAdornment,
  Fab,
  Checkbox,
} from "@mui/material";
import TaskAltIcon from "@mui/icons-material/TaskAlt";
import TaskIcon from "@mui/icons-material/Task";
import AddTaskIcon from "@mui/icons-material/AddTask";
import CheckCircleOutlineIcon from "@mui/icons-material/CheckCircleOutline";
import CheckCircleIcon from "@mui/icons-material/CheckCircle";
import DeleteForeverOutlinedIcon from "@mui/icons-material/DeleteForeverOutlined";
import EditIcon from "@mui/icons-material/Edit";

function Header() {
  const [numberOfTasks, setNumberOfTasks] = useState(0);

  const handleClick = () => {
    setNumberOfTasks((prevNumberOfTasks) => prevNumberOfTasks + 1);
    // alert(`Number of Taksks ${numberOfTasks}`);
  };
  useEffect(() => {
    axios
      .get("http://localhost:4000")
      .then((res) => {
        console.log(res.data.tasks);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [numberOfTasks]);
  const navBarComponent = (
    <AppBar position="static">
      <Toolbar>
        <IconButton size="large" edge="start" color="inherit">
          <TaskAltIcon></TaskAltIcon>
        </IconButton>
        <Typography
          variant="h6"
          component="div"
          sx={{ flexGrow: 1, textAlign: "left" }}
        >
          Task Manager
        </Typography>
      </Toolbar>
    </AppBar>
  );

  const taskComponent = (taskContent) => {
    return (
      <Stack direction="row">
        <Checkbox
          icon={<CheckCircleOutlineIcon />}
          checkedIcon={<CheckCircleIcon />}
        ></Checkbox>
        <Typography variant="h6" sx={{ flexGrow: 1 }}>
          {taskContent}
        </Typography>
        <IconButton>
          <EditIcon />
        </IconButton>
        <IconButton>
          <DeleteForeverOutlinedIcon />
        </IconButton>
      </Stack>
    );
  };

  const cardHeader = (
    <CardHeader
      avatar={<TaskAltIcon></TaskAltIcon>}
      title="Create New Task"
      disableTypography={false}
      sx={{
        textAlign: "left",
        backgroundColor: "grey",
      }}
    ></CardHeader>
  );

  return (
    <>
      {navBarComponent}

      <Grid container sx={{ justifyContent: "center", my: 10 }}>
        <Grid item sx={{ display: "flex", flexDirection: "column" }}>
          <Card
            raised={true}
            sx={{
              width: 500,
              display: "flex",
              flexDirection: "column",
            }}
          >
            {cardHeader}

            <CardContent>
              <Stack direction="row">
                <TextField
                  variant="outlined"
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <TaskIcon />
                      </InputAdornment>
                    ),
                  }}
                  sx={{ flexGrow: 1 }}
                ></TextField>
                <Fab
                  color="primary"
                  aria-label="add"
                  sx={{ ml: 2 }}
                  onClick={() => {
                    handleClick();
                  }}
                >
                  <AddTaskIcon></AddTaskIcon>
                </Fab>
              </Stack>
            </CardContent>

            <CardContent>
              <Stack direction="column" sx={{ my: 2 }}>
                {[...Array(numberOfTasks)].map((item) => {
                  return taskComponent("Kunj");
                })}
              </Stack>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </>
  );
}

export default Header;
