import { useState, useEffect } from "react";
import axios from "axios";
// import "./Header.css";
import {
  Stack,
  Button,
  AppBar,
  Toolbar,
  IconButton,
  Typography,
  Card,
  CardActions,
  Box,
  CardContent,
  CardHeader,
  Grid,
  TextField,
  InputAdornment,
  Fab,
  Checkbox,
  Divider,
} from "@mui/material";
import TaskAltIcon from "@mui/icons-material/TaskAlt";
import TaskIcon from "@mui/icons-material/Task";
import AddTaskIcon from "@mui/icons-material/AddTask";
import CheckCircleOutlineIcon from "@mui/icons-material/CheckCircleOutline";
import CheckCircleIcon from "@mui/icons-material/CheckCircle";
import DeleteForeverOutlinedIcon from "@mui/icons-material/DeleteForeverOutlined";
import EditIcon from "@mui/icons-material/Edit";

function Header() {
  const [numberOfTasks, setNumberOfTasks] = useState(0);
  const [taskList, setTaskList] = useState([]);

  const handleClick = () => {
    setNumberOfTasks((prevNumberOfTasks) => prevNumberOfTasks + 1);
    // alert(`Number of Taksks ${numberOfTasks}`);
  };
  useEffect(() => {
    axios
      .get("http://localhost:4000")
      .then((res) => {
        console.log(res.data.tasks);
        setTaskList(res.data.tasks);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [numberOfTasks]);
  const navBarComponent = (
    <AppBar position="static">
      <Toolbar>
        <IconButton size="large" edge="start" color="inherit">
          <TaskAltIcon></TaskAltIcon>
        </IconButton>
        <Typography
          variant="h6"
          component="div"
          sx={{ flexGrow: 1, textAlign: "left" }}
        >
          Task Manager
        </Typography>
      </Toolbar>
    </AppBar>
  );

  const taskComponent = (taskContent) => {
    return (
      <Stack
        direction="row"
        alignItems="center"
        // sx={{ border: "1px solid blue", my: 0.6, borderRadius: 10 }}
        // sx={{ backgroundColor: "grey" }}
      >
        <Checkbox
          icon={<CheckCircleOutlineIcon />}
          checkedIcon={<CheckCircleIcon />}
        ></Checkbox>
        <Typography variant="body1" sx={{ flexGrow: 1, textAlign: "left" }}>
          {taskContent}
        </Typography>
        <IconButton>
          <EditIcon />
        </IconButton>
        <IconButton>
          <DeleteForeverOutlinedIcon />
        </IconButton>
      </Stack>
    );
  };

  const cardHeader = (title) => {
    return (
      <CardHeader
        avatar={<TaskAltIcon></TaskAltIcon>}
        title={title}
        disableTypography={false}
        sx={{
          textAlign: "left",
          backgroundColor: "grey",
        }}
      ></CardHeader>
    );
  };

  return (
    <>
      {navBarComponent}

      <Grid container direction="column" sx={{ alignItems: "center", my: 15 }}>
        <Grid item sx={{ display: "flex", flexDirection: "column" }}>
          <Card
            raised={true}
            sx={{
              width: 500,
              display: "flex",
              flexDirection: "column",
            }}
          >
            {cardHeader("Create New Task")}

            <CardContent>
              <Stack direction="row">
                <TextField
                  variant="outlined"
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <TaskIcon />
                      </InputAdornment>
                    ),
                  }}
                  sx={{ flexGrow: 1 }}
                ></TextField>
                <Fab
                  color="primary"
                  aria-label="add"
                  sx={{ ml: 2 }}
                  onClick={() => {
                    handleClick();
                  }}
                >
                  <AddTaskIcon></AddTaskIcon>
                </Fab>
              </Stack>
            </CardContent>
            {/* <Divider sx={{ my: 3 }}></Divider> */}
            <CardContent sx={{ my: 1 }}>
              <Typography variant="h3">Task List</Typography>
            </CardContent>
            <Divider></Divider>
            {/* <CardContent raised="true">{taskComponent("testing")}</CardContent>
            <CardContent>{taskComponent("testing")}</CardContent>
            <CardContent>{taskComponent("testing")}</CardContent>
            <CardContent>{taskComponent("testing")}</CardContent> */}

            <CardContent sx={{ border: "1px black" }}>
              <Stack
                direction="column"
                spacing={2}
                divider={
                  <Divider
                    variant="fullWidth"
                    flexItem
                    orientation="horizontal"
                  ></Divider>
                }
                sx={{ my: 2, border: "1px black" }}
              >
                {taskList.map((item) => {
                  return taskComponent(item.title);
                })}
              </Stack>
            </CardContent>
          </Card>
        </Grid>
        <Grid item>
          <Card>{cardHeader("Your Tasks")}</Card>
        </Grid>
      </Grid>
    </>
  );
}

export default Header;
